mapper.select:
SELECT id as playlist_id, title, date_format(created_date, '%Y%m%d%H%i%s') as created_date 
  FROM playlists
 WHERE user_id = #{userId}
 ORDER BY id DESC
 LIMIT #{page}, #{perPage}

SELECT id as playlist_id, title, date_format(created_date, '%Y%m%d%H%i%s') as created_date 
  FROM playlists 
 WHERE user_id = #{user_id} 
   AND id = #{playlist_id}

mapper.delete:
DELETE FROM playlists 
 WHERE user_id = #{user_id} 
   AND id = #{playlist_id}
